$A.componentService.addComponentClass("markup://DRCTS:TileListView",function() {
return {
  "meta":{
    "name":"DRCTS$TileListView",
    "extends":"markup://aura:component"
  },
  "controller":{
    "doInit":function(component, event, helper) {
        helper.getTileSearchedValues(component);
    },
    "renderDetailView":function(component, event, helper) {
        if (!component.get('v.isDetailView') && component.get('v.directoriesObj.enableDetailView')) {
            helper.renderDetailView(component, event);
        }
    },
    "previousPage":function(component, event, helper) {
        helper.previousPage(component, event);
    },
    "nextPage":function(component, event, helper) {
        helper.nextPage(component, event);
    },
    "handleFieldUpdateEvent":function(component, event, helper) {
        helper.handleFieldUpdateEvent(component, event);
    },
    "collapsePanel":function(component, helper) {
        var panel = component.find('collapsePanel');
        var icon = component.find('collapsePanelIcon');
        $A.util.toggleClass(panel, 'fonteva-hide');
        $A.util.toggleClass(icon, 'fonteva-collapsed');
    }
  },
  "helper":{
    "getTileSearchedValues":function(component) {
        component.find('currentPageNumber').updateValue(1, false);
        var directoriesObj = component.get('v.directoriesObj');
        component.set('v.pages', _.ceil(_.divide(directoriesObj.sObjIds.length, directoriesObj.tilePageSize)));
        this.fetchPaginationRecss(component);
    },
    "renderDetailView":function(component, event) {
        var compEvent = $A.get('e.DRCTS:RenderDetailView');
        compEvent.setParams({
            recordId: event.currentTarget.dataset.id
        });
        compEvent.fire();
    },
    "previousPage":function(component, event) {
        var compEvent = $A.get('e.DRCTS:nextPreviousEvent');
        compEvent.setParams({
            onClickOrComplete : true
        });
        compEvent.fire();

        component.find('currentPageNumber').updateValue(_.subtract(component.get('v.tilePaginationObj').currentPageNumber, 1), false);
        this.loadPageResults(component);
    },
    "nextPage":function(component, event) {
        var compEvent = $A.get('e.DRCTS:nextPreviousEvent');
        compEvent.setParams({
            onClickOrComplete : true
        });
        compEvent.fire();

        component.find('currentPageNumber').updateValue(_.add(component.get('v.tilePaginationObj').currentPageNumber, 1), false);
        this.loadPageResults(component);
    },
    "handleFieldUpdateEvent":function(component, event, helper) {
        if (event.getParam('fieldId') === 'currentPageNumber' && component.get('v.tilePaginationObj').currentPageNumber !== 0
                && component.get('v.tilePaginationObj').currentPageNumber <= component.get('v.pages')) {
            this.fetchPaginationRecss(component);
        }
    },
    "fetchPaginationRecss":function(component) {
        var directoriesObj = component.get('v.directoriesObj');
        var action = component.get("c.fetchPaginationRecs");
        action.setParams({
            'directoriesId' : directoriesObj.directoriesId,
            'baseQueryWhereClause' : directoriesObj.baseQueryWhereClause,
            'filterWhereClause' : directoriesObj.filterWhereClause,
            'radiusWhereClause' : '',
            'sObjIds' : directoriesObj.sObjIds,
            'pageNumber' : component.get('v.tilePaginationObj').currentPageNumber || 1,
            'pageSize' : directoriesObj.tilePageSize,
            'sortField' : directoriesObj.selSortFieldName,
            'sortOrder' : directoriesObj.selSortFieldType,
            'defaultSortField' : directoriesObj.defaultSortField,
            'defaultSortOrder' : directoriesObj.defaultSortOrder
        });
        action.setCallback(this,function(response){
            if (response.getState() === 'SUCCESS') {
                var responseObj = response.getReturnValue();
                responseObj.forEach(function(responseEl){
                    for (var key in responseEl) {
                        if (key.startsWith('position') && key.endsWith('Value')) {
                            responseEl[key] = EscapeUtil.containsHtml(responseEl[key]) ? responseEl[key] : secureFilters.html(responseEl[key]);
                        }
                    }
                });
                component.set('v.tileSearchedValues', responseObj);
                this.updatePageResults(component, component.get('v.tilePaginationObj').currentPageNumber || 1, responseObj.length, directoriesObj);
                $A.util.removeClass(document.getElementById('fonteva-directories-results--container'), 'slds-hide');
                $A.util.addClass(document.getElementById('searchLoading'), 'slds-hide');

                var compEvent = $A.get('e.DRCTS:nextPreviousEvent');
                compEvent.setParams({
                    onClickOrComplete : false
                });
                compEvent.fire();
            } else {
                response.getError().forEach(function(error){
                    FontevaHelper.showErrorMessage(error.message);
                });
            }
        });
        $A.enqueueAction(action);
    },
    "updatePageResults":function(component, currentPageNum, currentPageSize, directoriesObj) {
        var compEvent = $A.get('e.DRCTS:PageResultsEvent');
        compEvent.setParams({
            startIndex : (directoriesObj.tilePageSize > 0) ? _.multiply(currentPageNum - 1, directoriesObj.tilePageSize) + 1 : 0,
            endIndex : _.multiply(currentPageNum - 1, directoriesObj.tilePageSize) + currentPageSize,
            recordCount : currentPageSize > 0 ? directoriesObj.sObjIds.length : 0
        });
        compEvent.fire();
    },
    "loadPageResults":function(component) {
        this.fetchPaginationRecss(component);
        if (!component.get('v.isDetailView')) {
            $A.util.addClass(document.getElementById('fonteva-directories-results--container'), 'slds-hide');
            $A.util.removeClass(document.getElementById('searchLoading'), 'slds-hide');
            window.scrollTo(0,0);
        }
    }
  }
};
});

$A.componentService.addComponentClass("markup://DRCTS:SortSelect",function() {
return {
  "meta":{
    "name":"DRCTS$SortSelect",
    "extends":"markup://aura:component"
  },
  "controller":{
    "handleClick":function(component, event, helper) {
        helper.handleClick(component, event);
    },
    "handleListClick":function(component, event, helper) {
        helper.handleListClick(component, event);
    }
  },
  "helper":{
    "handleClick":function(component, event, helper) {
        var eles = document.getElementsByClassName('fonteva-is-open');
        var parent = event.getSource().elements[0].parentElement;

        if (parent.classList.contains('fonteva-is-open')) {
            $A.util.removeClass(parent, 'fonteva-is-open');
        } else {
            $A.util.removeClass(eles[0], 'fonteva-is-open');
            $A.util.removeClass(eles[1], 'fonteva-is-open');
            $A.util.addClass( parent ,'fonteva-is-open');
        }
        event.stopPropagation();
    },
    "handleListClick":function(component, event, helper) {
        $A.util.toggleClass(document.getElementById('selectNubbin'), 'hide');
        var compEvent = $A.get('e.DRCTS:SortDropDown');
        if (component.get('v.sortBy') === 'Radius' && event.currentTarget.dataset.value === component.get('v.currentSortField')) {
            compEvent.setParams({
                fieldName: 'All',
                sortBy : component.get('v.sortBy')
            });
            component.set('v.currentSortField', null);
        } else {
            compEvent.setParams({
                fieldName: event.currentTarget.dataset.value,
                sortType : this.getSortDirection(component, event.currentTarget.dataset.value),
                sortBy : component.get('v.sortBy')
            });
        }
        compEvent.fire();

        var eles = document.getElementsByClassName('selectPicklist');
        if (eles.length > 1) {
            for (var i=0; i < eles.length; i++) {
                if (eles[i].className.indexOf('fonteva-is-open') > -1) {
                    $A.util.removeClass(eles[i], 'fonteva-is-open');
                }
            }
        } else {
            if (eles.className.indexOf('fonteva-is-open') > -1) {
                $A.util.removeClass(eles, 'fonteva-is-open');
            }
        }
    },
    "getSortDirection":function(component, value) {
        var currentSortField = component.get('v.currentSortField');
        var currentSortDirection = component.get('v.currentSortDirection');
        if (currentSortField && currentSortDirection) {
            if (value === currentSortField) {
                if (currentSortDirection === 'asc') {
                    currentSortDirection = 'desc';
                } else {
                    currentSortDirection = 'asc';
                }
            } else {
                currentSortDirection = 'asc';
                currentSortField = value;
            }
        } else if (!currentSortField) {
            currentSortField = value;
        }
        component.set('v.currentSortField', currentSortField);
        component.set('v.currentSortDirection', currentSortDirection);
        return component.get('v.currentSortDirection');
    }
  }
};
});

$A.componentService.addComponentClass("markup://DRCTS:GeoListView",function() {
return {
  "meta":{
    "name":"DRCTS$GeoListView",
    "extends":"markup://aura:component"
  },
  "controller":{
    "doInit":function(component, event, helper) {
        helper.getGeoSearchedValues(component);
    },
    "renderDetailView":function(component, event, helper) {
        if (!component.get('v.isDetailView') && component.get('v.directoriesObj.directoryRecord.enableDetailView')) {
            helper.renderDetailView(component, event);
        }
    },
    "previousPage":function(component, event, helper) {
        helper.previousPage(component, event);
    },
    "nextPage":function(component, event, helper) {
        helper.nextPage(component, event);
    },
