        var directoriesObj = component.get('v.directoriesObj');
        var filterNameValues = {};
        var filterNameIndex;

        if (!$A.util.isEmpty(directoriesObj.filterObj) && !$A.util.isEmpty(directoriesObj.filterObj[_.findIndex(directoriesObj.filterObj, {filterName : filterObj.filterName})])) {
            filterNameIndex = _.findIndex(directoriesObj.filterObj, {filterName : filterObj.filterName});
            filterNameValues = directoriesObj.filterObj[filterNameIndex];
        } else {
            filterNameValues = filterObj;
            filterNameValues.filteredValues = [];
        }
        var isCheck = false;
        var isRange = false;
        if (!$A.util.isEmpty(filterObj.filterType)) {
            switch(filterObj.filterType.toLowerCase()) {
                case 'multipicklist':
                case 'picklist' :
                case 'boolean' :
                    isCheck = true;
                    break;
                case 'currency' :
                case 'double' :
                case 'date' :
                case 'datetime' :
                case 'decimal' :
                case 'integer' :
                case 'long':
                    isRange = true;
                    break;
                default :
                    break;
            }
            if (isCheck) {
                if (filterObj.filterValue) {
                    filterNameValues.filteredValues.push(filterObj.whatLabel);
                } else {
                    _.pull(filterNameValues.filteredValues, filterObj.whatLabel);
                }
            } else if (isRange) {
                if (filterObj.leftRightRange === 'left') {
                    filterNameValues.filterRangeLeftValue = filterObj.filterValue;
                } else if (filterObj.leftRightRange === 'right') {
                    filterNameValues.filterRangeRightValue = filterObj.filterValue;
                }
            }
            var existedFilters = directoriesObj.filterObj;
            if (!$A.util.isEmpty(filterNameIndex)) {
                existedFilters[filterNameIndex] = filterNameValues;
            } else {
                existedFilters.push(filterNameValues);
            }
            directoriesObj.filterObj = existedFilters;
            component.set('v.directoriesObj', directoriesObj);
        }
        this.filterRecs(component);
