        helper.printList(component, event);
    }
  },
  "helper":{
    "doInit":function(component, event) {
        var directoriesObj = component.get('v.directoriesObj');
        component.find('currentPageNumber').updateValue(1, false);
        var fieldTypes = directoriesObj.sWrapper.fieldTypes;
        var tableColumnHeaders = [];
        var columnHeadersArray = directoriesObj.columnHeaders;
        if (Array.isArray(columnHeadersArray)) {
            for (var i=0; i < columnHeadersArray.length; i++) {
                tableColumnHeaders.push({value : _.split(columnHeadersArray[i].label,' (')[0], key : columnHeadersArray[i].value, isSortable : fieldTypes[columnHeadersArray[i].value].isSortable});
            }
        }
        component.set('v.tableColumnHeaders', tableColumnHeaders);
        component.set('v.pages', _.ceil(_.divide(directoriesObj.sObjIds.length, directoriesObj.tablePageSize)));
        this.fetchPaginationRecss(component);
    },
    "sortList":function(component, event) {
        var fieldName, sortType, ele;
        if (event.currentTarget.children.length > 0) {
            ele = event.currentTarget;
        } else {
            ele = event.srcElement;
        }
        fieldName = ele.dataset.fieldname;
        sortType = $A.util.hasClass(ele.children[0], 'slds-hide') ? 'desc' : 'asc';
        if (sortType === 'desc') {
            $A.util.removeClass(ele.children[0], 'slds-hide');
            $A.util.addClass(ele.children[1], 'slds-hide');
        } else {
            $A.util.addClass(ele.children[0], 'slds-hide');
            $A.util.removeClass(ele.children[1], 'slds-hide');
        }
        var directoriesObj = component.get('v.directoriesObj');
        directoriesObj.selSortFieldName = fieldName;
        directoriesObj.selSortFieldType = sortType;
        component.set('v.directoriesObj', directoriesObj);
        var tableHeaders = document.getElementsByClassName('tableHeaders');
        for (var i=0; i < tableHeaders.length; i++) {
            if (fieldName !== tableHeaders[i].dataset.fieldname) {
                for (var j=0; j < tableHeaders[i].children.length; j++) {
                    tableHeaders[i].children[j].classList.add('slds-hide');
                }
            }
        }
        this.fetchPaginationRecss(component);
