            onClickOrComplete : true
        });
        compEvent.fire();

        component.find('currentPageNumber').updateValue(_.subtract(component.get('v.tablePaginationObj').currentPageNumber, 1), false);
        this.loadPageResults(component);
    },
    "nextPage":function(component, event) {
        var compEvent = $A.get('e.DRCTS:nextPreviousEvent');
        compEvent.setParams({
            onClickOrComplete : true
        });
        compEvent.fire();

        component.find('currentPageNumber').updateValue(_.add(component.get('v.tablePaginationObj').currentPageNumber, 1), false);
        this.loadPageResults(component);
    },
    "handleFieldUpdateEvent":function(component, event) {
        if (event.getParam('fieldId') === 'currentPageNumber' && component.get('v.tablePaginationObj').currentPageNumber !== 0
                && component.get('v.tablePaginationObj').currentPageNumber <= component.get('v.pages')) {
            this.fetchPaginationRecss(component);
        }
    },
    "fetchPaginationRecss":function(component) {
        var directoriesObj = component.get('v.directoriesObj');
        var action = component.get("c.fetchPaginationRecs");
        action.setParams({
            'directoriesId' : directoriesObj.directoriesId,
            'baseQueryWhereClause' : directoriesObj.baseQueryWhereClause,
            'filterWhereClause' : directoriesObj.filterWhereClause,
            'radiusWhereClause' : '',
            'sObjIds' : directoriesObj.sObjIds,
            'pageNumber' : component.get('v.tablePaginationObj').currentPageNumber || 1,
            'pageSize' : directoriesObj.tablePageSize,
            'sortField' : directoriesObj.selSortFieldName,
            'sortOrder' : directoriesObj.selSortFieldType,
            'defaultSortField' : directoriesObj.defaultSortField,
            'defaultSortOrder' : directoriesObj.defaultSortOrder
        });
        action.setCallback(this,function(response){
            if (response.getState() === 'SUCCESS') {
                component.set('v.directoriesObj.currentPageRecs', response.getReturnValue());
                this.setPaginationRecs(component);
            } else {
                response.getError().forEach(function(error){
                    FontevaHelper.showErrorMessage(error.message);
                });
            }
        });
        $A.enqueueAction(action);
    },
    "setPaginationRecs":function(component) {
        var directoriesObj = component.get('v.directoriesObj');
        var fieldTypes = directoriesObj.sWrapper.fieldTypes;
        var currentPageSObjs = [];
        if (!$A.util.isEmpty(directoriesObj.currentPageRecs)) {
            var currentPageRecs = JSON.parse(directoriesObj.currentPageRecs);
            var columnHeadersArray = directoriesObj.columnHeaders;
            for (var i=0; i < currentPageRecs.length; i++) {
                var mapOfRecords = [];
                var currentRec = currentPageRecs[i];
                for (var index=0; index < columnHeadersArray.length; index++) {
                    const columnHeaderFieldType = fieldTypes[columnHeadersArray[index].value].fieldType.toLowerCase();
                    const sobjRecord = {
                        reflabel: _.split(columnHeadersArray[index].label, ' (')[0],
                        value: currentRec[columnHeadersArray[index].value],
                        key: columnHeadersArray[index].value,
                        type: columnHeaderFieldType
                    }
                    if (sobjRecord.value == null) {
                        sobjRecord.value = '';
                    }
                    if (columnHeaderFieldType === 'date' && !$A.util.isEmpty(sobjRecord.value)) {
                        sobjRecord.value = moment(sobjRecord.value).format(directoriesObj.userDateFormat);
                    } else if (columnHeaderFieldType === 'datetime' && !$A.util.isEmpty(sobjRecord.value)) {
                        sobjRecord.value = moment(sobjRecord.value).format(directoriesObj.userDateFormat + ' HH:mm');
                    } else if (columnHeaderFieldType === 'address') {
                        if (sobjRecord.value != '') {
                            sobjRecord.value = this.formAddress(sobjRecord.value);
                        } else {
                            sobjRecord.value = '';
                        }
                    } else if (columnHeaderFieldType === 'url' && !$A.util.isEmpty(sobjRecord.value)) {
                            sobjRecord.value = (sobjRecord.value.indexOf('http://') > -1 || sobjRecord.value.indexOf('https://') > -1) ? sobjRecord.value : 'http://' + sobjRecord.value;
                        } else {
                            sobjRecord.value = EscapeUtil.containsHtml(sobjRecord.value) ? sobjRecord.value : secureFilters.html(sobjRecord.value);
                    }
                    mapOfRecords.push(sobjRecord);
                }
                currentPageSObjs.push({recInfo : mapOfRecords, id : currentPageRecs[i].Id});
            }
        }
        component.set('v.currentPageSObjs', currentPageSObjs);
        var currentPageNum = component.get('v.tablePaginationObj').currentPageNumber || 1;
        directoriesObj.currentPageNumber = currentPageNum;
        component.set('v.directoriesObj', directoriesObj);
        this.updatePageResults(component, currentPageNum, currentPageSObjs.length, directoriesObj);
        if (currentPageSObjs.length > 0) {
            $A.util.addClass(component.find('noResults'), 'slds-hide');
        } else {
            $A.util.removeClass(component.find('noResults'), 'slds-hide');
        }
        $A.util.addClass(document.getElementById('searchLoading'), 'slds-hide');
        $A.util.removeClass(document.getElementById('fonteva-directories-results--container'), 'slds-hide');


        var compEvent = $A.get('e.DRCTS:nextPreviousEvent');
        compEvent.setParams({
            onClickOrComplete : false
        });
        compEvent.fire();
    },
    "formAddress":function(addressVal) {
        if (!$A.util.isEmpty(addressVal)) {
            return (typeof (addressVal['street'] === 'undefined' || addressVal['street'] == null) ? '' : (addressVal['street'])) +
                (typeof (addressVal['city'] === 'undefined'  || addressVal['street'] == null) ? '' : (", " + addressVal['city'])) +
                (typeof (addressVal['state'] === 'undefined'  || addressVal['street'] == null) ? '' : (", " + addressVal['state'])) +
                (typeof (addressVal['postalCode'] === 'undefined'  || addressVal['street'] == null) ? '' : (", " + addressVal['postalCode'])) +
                (typeof (addressVal['country'] === 'undefined'  || addressVal['street'] == null) ? '' : (", " + addressVal['country']));
        }
    },
    "printList":function(component, event) {
