        setDirectoryMetadata.directoriesId = directoriesObj.directoriesId;
        setDirectoryMetadata.sObjIds = directoriesObj.sObjIds;
        setDirectoryMetadata.selSortFieldName = directoriesObj.selSortFieldName;
        setDirectoryMetadata.selSortFieldType = directoriesObj.selSortFieldType;
        setDirectoryMetadata.fieldTypes = directoriesObj.sWrapper.fieldTypes;
        localStorage.setItem(directoriesInfo, JSON.stringify(setDirectoryMetadata));
        var win = window.open('/'+directoriesObj.nsp+'directories_printlist?id='+directoriesObj.directoriesId, '_blank');
        win.focus();
    },
    "updatePageResults":function(component, currentPageNum, currentPageSize, directoriesObj) {
        var compEvent = $A.get('e.DRCTS:PageResultsEvent');
        compEvent.setParams({
            startIndex : (directoriesObj.tablePageSize > 0) ? _.multiply(currentPageNum - 1, directoriesObj.tablePageSize) + 1 : 0,
            endIndex : _.multiply(currentPageNum - 1, directoriesObj.tablePageSize) + currentPageSize,
            recordCount : currentPageSize > 0 ? directoriesObj.sObjIds.length : 0
        });
        compEvent.fire();
    },
    "loadPageResults":function(component) {
        this.fetchPaginationRecss(component);
        if (!component.get('v.isDetailView')) {
            $A.util.addClass(document.getElementById('fonteva-directories-results--container'), 'slds-hide');
            $A.util.removeClass(document.getElementById('searchLoading'), 'slds-hide');
            window.scrollTo(0,0);
        }
    }
  },
  "renderer":{
    "afterRender":function(component, helper) {
        component.superAfterRender();
        var directoriesObj = component.get('v.directoriesObj');
        var sortType = directoriesObj.selSortFieldType || (directoriesObj.defaultSortOrder || 'asc');
        var sortFieldName = directoriesObj.selSortFieldName || directoriesObj.defaultSortField;
        if ($A.util.isEmpty(sortFieldName)) {
             sortFieldName = !$A.util.isEmpty(directoriesObj.columnHeaders) ? directoriesObj.columnHeaders[0].value : 'Name';
