$A.componentService.addComponentClass("markup://Framework:QaData",function() {
return {
  "meta":{
    "name":"Framework$QaData",
    "extends":"markup://aura:component"
  },
  "helper":{
    "buildDataAttributes":function(cmp) {
        setTimeout(
            $A.getCallback(function () {
                var dataObj = {};
                var strJson = cmp.get('v.qaData');
                try {
                    if (typeof strJson === 'string') {
                        try {
                            dataObj = JSON.parse(strJson);
                        } catch (err) {}
                        if (!dataObj && strJson) {


                            strJson = strJson.replace(/(['"])?([a-zA-Z0-9_]+)(["'])?/g, '"$2"');
                            dataObj = JSON.parse(strJson);
                        }
                    }
                    var superCmp = cmp.getConcreteComponent();
                    if (!dataObj || Object.keys(dataObj).length === 0) {
                        var name = superCmp.getLocalId();
                        if (name) {
                            dataObj.name = name;
                        }
                        var label =
                            superCmp.getName().includes('InputFields') || superCmp.getName().includes('Button')
                                ? superCmp.get('v.label')
                                : null;
                        if (label) {
                            dataObj.label = label;
                        }
                    }
                    var div = superCmp.getName().includes('InputFields')
                        ? superCmp.get('v.fieldType').toLowerCase() === 'boolean'
                            ? superCmp.find('booleanInputFieldHere')
                            : superCmp.get('v.fieldType').toLowerCase() === 'radio'
                            ? superCmp.find('radioInputFieldHere')
                            : superCmp.find('inputFieldHere')
                        : superCmp.getName().includes('PicklistInputField')
                        ? superCmp.find('select')
                        : superCmp.find('localButton');

                    var divList = [div];
                    divList = divList.concat(superCmp.find('tagWithQaData'));
                    divList.forEach(function (div) {
                        Object.keys(dataObj).forEach(function (key) {
                            if (div) {
                                div.getElement().setAttribute('data-' + key, dataObj[key]);
                            } else if (sessionStorage.getItem('fon.log') === 'vv') {
                                console.log(`some bad div in ${divList}`);
                            }
                        });
                    });
                } catch (err) {
                    console.log('failed adding QaData. Most likely due to non-quoted JSON keys.');
                    console.log(cmp.get('v.qaData'));
                    console.log(strJson);
                    console.log(err);
                }
            }),
            200
        );
    }
  },
  "renderer":{
    "afterRender":function(cmp, helper) {
        cmp.superAfterRender();
        helper.buildDataAttributes(cmp);
    }
  }
};
});

