            title: params.title,
            titleColor: '',
            message: params.message,
            messageColor: '',
            backgroundColor: '',
            color: color, 
            icon: '',
            iconText: '',
            iconColor: '',
            image: '',
            imageWidth: 50,
            zindex: 9999999999999,
            layout: 1,
            balloon: false,
            close: true,
            rtl: false,
            position: params.position, 
            target: '',
            targetFirst: true,
            timeout: timeout,
            drag: true,
            pauseOnHover: true,
            resetOnHover: false,
            progressBar: true,
            progressBarColor: '',
