                                var dependentFieldName = _.replace(ele.fieldName, 'Country', 'State');
                                defaultObj[dependentFieldName] = true;
                            }
                        });
                        component.set('v.preferenceValues', defaultObj);
                    }
                }
                component.set('v.editableFieldsWrappers', editFieldsValues.editableFieldsWrappers);
                var componentsToCreate = [],
                    preferenceFields = [];
                editFieldsValues.editableFieldsWrappers.forEach(function(ele) {


                    var profileObj = {},
                        preferenceObj = {},
                        preferenceDependentObj = {};
                    if (directoriesObj.directoryRecord.detailImageField === ele.fieldName) {
                        component.set('v.editImage', true);
