                    if (ele.isUpdateable && directoriesObj.directoryRecord.detailImageField !== ele.fieldName) {
                        var data = {},
                            preferenceData = {},
                            preferenceDependentData = {};
                        data.fieldType = ele.fieldType && ele.fieldType.toLowerCase();

                        var otherAttributes;
                        if (ele.fieldType && ele.fieldType.toLowerCase() === "multipicklist") {
                            otherAttributes = { objectName: directoriesObj.objectName, field: ele.fieldName };
                        } else {
                            otherAttributes = { allowCreate: false, selectOptions: ele.selectOptions };
                        }
                        otherAttributes.objectName = directoriesObj.objectName;


                        if (ele.isDependentField) {
                            otherAttributes.dependentField = ele.fieldName;
                            otherAttributes.parentControllingFieldKey = ele.controllingField;
