                            otherAttributes.selectOptions = ele.selectOptions;
                            otherAttributes.field = ele.fieldName;
                            otherAttributes.controllingFieldKey = ele.fieldName;
                        }
                        data.otherAttributes = otherAttributes;

                        data.group = 'edit-modal-fields';
                        data.label = ele.fieldLabel;
                        data.helpText = ele.fieldHelpText;
