                            preferenceData.group = 'preference-modal-fields';
                            preferenceData['aura:id'] = ele.fieldName;
                            if (ele.fieldType && ele.fieldType.toLowerCase() === 'boolean') {
                                preferenceData.styleClasses = 'fonteva-input--boolean';
                            } else if (ele.fieldType.toLowerCase() === 'textarea') {
                                preferenceData.styleClasses = 'fonteva-input--textarea';
                            } else {
                                preferenceData.styleClasses = 'fonteva-input--string';
                            }
                            componentsToCreate.push(['markup://Framework:InputFields', preferenceData]);


                            if (ele.isPicklistEnabled && !$A.util.isEmpty(ele.fieldName)) {
                                var dependentFieldName = _.replace(ele.fieldName, 'Country', 'State');
                                preferenceDependentData.fieldType = 'boolean';
                                preferenceDependentObj[dependentFieldName] = component.get('v.preferenceValues')[dependentFieldName];
                                preferenceDependentData.value = preferenceDependentObj;
                                preferenceDependentData.group = 'preference-modal-fields';
                                preferenceDependentData['aura:id'] = dependentFieldName;
                                preferenceDependentData.styleClasses = 'fonteva-input-dependent--string';
                                componentsToCreate.push(['markup://Framework:InputFields', preferenceDependentData]);
                            }
                        }
                        componentsToCreate.push(['markup://Framework:InputFields', data]);
                    }
                });
                if (componentsToCreate.length > 0) {
                    $A.createComponents(componentsToCreate,
                        function(components, status, errorMessage) {
                            if (status === "SUCCESS") {
                                var editValuesUpdated = [],
                                    preferenceValuesUpdated = [];
                                for (var i = 0; i < components.length; i++) {

                                    if (components[i].get('v.group') === 'edit-modal-fields') {
                                        editValuesUpdated.push(components[i].get('v.value'));
                                    } else {
                                        preferenceValuesUpdated.push(components[i].get('v.value'));
                                    }
                                }
                                component.set('v.preferenceValuesUpdated', preferenceValuesUpdated);
                                component.set('v.editValuesUpdated', editValuesUpdated);
                                var divComponent = component.find('edit-modal-fields');
                                divComponent.set("v.body", components);
                            } else if (status === "ERROR") {
                                response.getError().forEach(function(error) {
                                    component.find('toastMessages').showMessage('', error.message, false, 'error', 'topCenter');
                                });
                            }
                        }
                    );
                }
                $A.util.addClass(component.find('modalProfileUpdateForm'), 'slds-fade-in-open');
                $A.util.addClass(component.find('modalBackdrop'), 'slds-backdrop--open');
            } else {
                response.getError().forEach(function(error) {
                    component.find('toastMessages').showMessage('', error.message, false, 'error', 'topCenter');
                });
            }
        });
        $A.enqueueAction(action);
    },
    "updateProfileCloseModal":function(component) {
        var directoriesObj = component.get('v.directoriesObj');
        var editValues = component.get('v.editValues');
        var editValuesUpdated = component.get('v.editValuesUpdated');
        var preferenceValuesUpdated = component.get('v.preferenceValuesUpdated');
        var profileObject = component.get('v.profileObject');
        for (var editValueUpdated in editValuesUpdated) {
            var key = Object.keys(editValuesUpdated[editValueUpdated]);
            var val = editValuesUpdated[editValueUpdated][key];

            if (directoriesObj.directoryRecord.objectName === 'Contact' || directoriesObj.directoryRecord.objectName === 'Account') {


                if (_.isObject(val) && _.has(val, ['primary'])) {
                    editValues[key] = val['primary'];
                    editValues[key + 'Code'] = val['primary'];
                    editValues[_.replace(key, 'Country', 'StateCode')] = val['dependent'];
                    delete editValues[key];
                } else {
                    editValues[key] = val;
                }
            } else {
                editValues[key] = val;
            }
        }
        let editableFieldsWrappers = component.get('v.editableFieldsWrappers');
        for (let index = 0; index < editableFieldsWrappers.length; index++) {
            let field = editableFieldsWrappers[index];
            if (field.fieldType && field.fieldType.toLowerCase() === "multipicklist" && editValues[field.fieldName]) {
                editValues[field.fieldName] = editValues[field.fieldName].replaceAll(',', ';');
            }
        }
        component.set('v.editValues', editValues);
        var preferenceValues = component.get('v.preferenceValues');
        for (var index in preferenceValuesUpdated) {
            var property = Object.keys(preferenceValuesUpdated[index]);
            preferenceValues[property] = preferenceValuesUpdated[index][property];
        }
        component.set('v.preferenceValues', preferenceValues);
        var directoriesObj = component.get('v.directoriesObj');
        var action = component.get("c.updateEditFields");
        var editValues = component.get('v.editValues');
        delete editValues.OtherCountryCode;
        delete editValues.OtherStateCode;

        action.setParams({
            updatedValues: JSON.stringify(editValues),
            preferenceValues: JSON.stringify(component.get('v.preferenceValues')),
            recordId: component.get('v.recordId'),
            imageUrl: profileObject.profileImageUrl,
            imageField: directoriesObj.directoryRecord.detailImageField
        });
        action.setCallback(this, function(response) {
            if (response.getState() === 'SUCCESS') {
                var responseObj = response.getReturnValue();
                editValues = JSON.parse(responseObj);
                component.set('v.editValues', editValues);
                directoriesObj.detailImageFieldValue = profileObject.profileImageUrl;
                if (_.hasIn(editValues, directoriesObj.directoryRecord.nameField)) {
                    directoriesObj.nameFieldValue = editValues[directoriesObj.directoryRecord.nameField];
                }
                if (_.hasIn(editValues, directoriesObj.directoryRecord.detailSubtitleField)) {
                    directoriesObj.detailSubtitleFieldValue = editValues[directoriesObj.directoryRecord.detailSubtitleField];
                }
                directoriesObj.selRec = responseObj;
                component.set('v.directoriesObj', directoriesObj);
                this.setAdditionalFields(component);
                $A.util.removeClass(component.find('modalProfileUpdateForm'), 'slds-fade-in-open');
                $A.util.removeClass(component.find('modalBackdrop'), 'slds-backdrop--open');
            } else {
                response.getError().forEach(function(error) {
                    component.find('toastMessages').showMessage('', error.message, false, 'error', 'topCenter');
                });
            }
        });
        $A.enqueueAction(action);
    },
    "setOptions":function(component, componentName, options, value) {
        component.find(componentName).setSelectOptions(options, value);
    },
    "setImageCropping":function(component) {
        var directoriesObj = component.get('v.directoriesObj');
        var otherAttributes = {
            maximumFileSize: 15242880,
            allowedMimeTypes: ["image/png", "image/jpeg", "image/jpg", "image/gif", "image/bmp", "image/tiff"],
            croppingParams: {
                enableExif: true,
                viewport: {
                    width: 400,
                    height: 260,
                    type: 'square'
                },
                boundary: {
                    width: 600,
                    height: 300
                }
            },
            croppingResultParams: {
                type: "blob",
                size: { width: 400, height: 260 },
                format: "jpeg",
                circle: false
            },
            allowCropping: true
        };
        component.find('profileImageUrl').setOtherAttributes(otherAttributes);
    },
    "setAdditionalFields":function(component) {
        var self = this;
        var additionalFieldsValues = [],
            chosenPreferenceFieldObj = {};
        var directoriesObj = component.get('v.directoriesObj');
        var sobj = JSON.parse(directoriesObj.selRec);
        var additionalFields = directoriesObj.directoryRecord.additionalFields;
        var fieldTypes = directoriesObj.directoryRecord.sWrapper.fieldTypes;
        if (!$A.util.isEmpty(component.get('v.directoryPreferences')) && component.get('v.recordId') !== directoriesObj.primaryContactId) {
            chosenPreferenceFieldObj = JSON.parse(component.get('v.directoryPreferences'));
        }
        for (var property in additionalFields) {
            if (additionalFields.hasOwnProperty(property)) {
                var isPreferenceField = true;
                if (directoriesObj.directoryRecord.enableContactPreferences && component.get('v.recordId') !== directoriesObj.primaryContactId &&
                    !$A.util.isEmpty(chosenPreferenceFieldObj) && chosenPreferenceFieldObj.hasOwnProperty(additionalFields[property].value) && !chosenPreferenceFieldObj[additionalFields[property].value]) {
                    isPreferenceField = false;
                }
                if (isPreferenceField) {
                    var additionalFieldType = fieldTypes[additionalFields[property].value].fieldType.toLowerCase();
                    var fieldVal = sobj[additionalFields[property].value];
                    var fieldLabel = _.split(fieldTypes[additionalFields[property].value].label, ' (')[0];
                    if (typeof fieldVal !== 'undefined') {
                        switch (additionalFieldType) {
                            case 'date':
                                additionalFieldsValues.push({ value: moment(fieldVal).format(directoriesObj.userDateFormat), fieldType: additionalFieldType, label: fieldLabel });
                                break;
                            case 'datetime':
                                additionalFieldsValues.push({ value: moment(fieldVal).format(directoriesObj.userDateFormat + ' HH:mm'), fieldType: additionalFieldType, label: fieldLabel });
                                break;
                            case 'address':
                                additionalFieldsValues.push({ value: self.formAddress(fieldVal), fieldType: additionalFieldType, label: fieldLabel });
                                break;
                            case 'url':
                                additionalFieldsValues.push({ value: (fieldVal.indexOf('http://') > -1 || fieldVal.indexOf('https://') > -1) ? fieldVal : 'http://' + fieldVal, fieldType: additionalFieldType, label: fieldLabel });
                                break;
                            default:
                                additionalFieldsValues.push({value : fieldVal, fieldType : additionalFieldType, label : fieldLabel})
                                break;
                        }
                    }
                }
            }
        }
        directoriesObj.additionalFieldsValues = _.compact(additionalFieldsValues);
        component.set('v.directoriesObj', directoriesObj);
    },
    "formAddress":function(addressVal) {
        if (!$A.util.isEmpty(addressVal)) {
            return ($A.util.isEmpty(addressVal['street']) ? '' : secureFilters.html(addressVal['street'])) +
                ($A.util.isEmpty(addressVal['city']) ? '' : (", " + secureFilters.html(addressVal['city']))) +
                ($A.util.isEmpty(addressVal['state']) ? '' : (", " + secureFilters.html(addressVal['state']))) +
                ($A.util.isEmpty(addressVal['postalCode']) ? '' : (", " + secureFilters.html(addressVal['postalCode']))) +
                ($A.util.isEmpty(addressVal['country']) ? '' : (", " + secureFilters.html(addressVal['country'])));
        }
    },
    "scrollSpy":function(component) {

        var section = document.querySelectorAll(".fonteva-sections > div > div");
        var sections = {};
        var i = 0;

        Array.prototype.forEach.call(section, function(e) {
            sections[e.id] = e.offsetTop;
        });

        window.addEventListener('scroll', function() {
            var scrollPosition = document.documentElement.scrollTop || document.body.scrollTop;

            for (i in sections) {
                if (sections[i] <= scrollPosition) {

                    document.querySelector('a[href*=' + sections[i].id + ']').setAttribute('class', 'section-active');
                }
            }
        });
    },
    "showHiddenFields":function(component) {
        if (document.getElementById('fontevaDetailFields').offsetHeight > 200) {
            document.getElementById('fontevaDetailFields').style.height = '200px';
            $A.util.removeClass(component.find('fieldButtonShow'), 'slds-hide');
            $A.util.addClass(component.find('fieldButtonHide'), 'slds-hide');
            $A.util.addClass(component.find('moreBtnContainer'), 'fonteva-button--more');
        } else {
            document.getElementById('fontevaDetailFields').style.height = 'auto'
            $A.util.addClass(component.find('fieldButtonShow'), 'slds-hide');
            $A.util.removeClass(component.find('fieldButtonHide'), 'slds-hide');
            $A.util.removeClass(component.find('moreBtnContainer'), 'fonteva-button--more');
        }
    }
  },
  "renderer":{
    "afterRender":function(component, helper) {
        component.superAfterRender();
        var winWidth = window.outerWidth;
        var directoriesObj = component.get('v.directoriesObj');

        var searchBarHeight = $('.fonteva-slds-search--navbar').outerHeight();

        if (winWidth >= 768 ) {
            document.body.style.paddingTop = _.toString(searchBarHeight + $('.fonteva-page-header').outerHeight())+'px';
        } else {
            document.body.style.paddingTop = _.toString(searchBarHeight)+'px';
        }

        window.addEventListener('resize', function() {
            winWidth = window.outerWidth;
            if (winWidth >= 768 ) {
                document.body.style.paddingTop = _.toString(searchBarHeight + $('.fonteva-page-header').outerHeight())+'px';
            } else {
                document.body.style.paddingTop = _.toString(searchBarHeight)+'px';
            }
        });



        if (document.querySelectorAll('.fonteva-sections .directories-wrapper > *').length > 0 || document.querySelectorAll('.detail-location-placeholder > *').length > 0) {
            if (document.getElementById('fontevaDetailFields').offsetHeight > 200) {
                document.getElementById('fontevaDetailFields').style.height = '200px';
            } else {
                $A.util.addClass(component.find('fieldButtonShow'),'slds-hide');
                $A.util.removeClass(component.find('moreBtnContainer'),'fonteva-button--more');
            }
        } else {
            $A.util.addClass(component.find('fieldButtonShow'),'slds-hide');
            $A.util.removeClass(component.find('moreBtnContainer'),'fonteva-button--more');
        }

        setTimeout($A.getCallback(function(){
            var childRelationshipsList = component.get('v.childRelationshipsList');
            if (childRelationshipsList.length  > 1) {
                $('a[id*='+childRelationshipsList[childRelationshipsList.length - 1].view+']').removeClass('fonteva-page-header--link_active');
            }
            $('.fonteva-custom--html:has(ul, li, dl, dt, dd, ol)').addClass('fonteva-custom--html-list');
        }), 250);
        if (window.scrollY === 0) {
            $("a[id*='Overview']").addClass('fonteva-page-header--link_active');
        }
        $('html, body').animate({ scrollTop: $('div[id=Overview]').offset().top - (($('.fonteva-slds-search--navbar').outerHeight() + $('.fonteva-page-header').outerHeight() + $('#backToResultsBtn').outerHeight())) }, 500);
    }
  }
};
});
